import React from 'react';
import { Route, Redirect, withRouter } from 'react-router-dom';
import { bool, any, object } from 'prop-types';

const ProtectedRouteHoc = ({ component: Component, isAuthenticated, ...rest }) => {
	if (isAuthenticated || rest.public) {
		return (
			<Route
				{...rest}
				render={props => {
					if (process.env.NODE_ENV === 'production') {
						ReactGA.pageview(props.match.url);
					}
					return <Component {...props}></Component>;
				}}
			/>
		);
	}
	return <Redirect to={{ pathname: '/' }} />;
};

ProtectedRoute.propTypes = {
	component: any,
	isAuthenticated: bool,
	rest: object,
	location: object,
	props: object,
	match: object,
};

export default withRouter(ProtectedRouteHoc);
